/opt/postgresql/bin/psql -U service-phigita -h mars -c "delete from xo.xo__book__biblionet; copy xo.xo__book__biblionet from '$1' with delimiter '|';delete from xo.xo__book__biblionet where isbn13 is null;delete from xo__book where ean13 in (select ean13 from xo.xo__book__biblionet b1, xo__book b2 where b1.isbn13=b2.ean13);insert into xo__book(ean13,biblionet_ref_id,image_p,authorlist,categories,author,description,isbn,pages,price,publisher,title,year,subtitle,width,height,edition,binding,prototype,extra) select isbn13,biblionet_ref_id,image_p,authorlist,cateogories,author,description,isbn10,pages,coalesce(price,'0')::numeric(10,2),publisher,title,year,subtitle,width,height,edition,cover_type,prototype_title,extra from xo.xo__book__biblionet where biblionet_ref_id > (select max(biblionet_ref_id) from xo__book) and isbn13 is not null and isbn13!='' order by isbn13;" bookdb
